import React, { useState } from 'react';
import Draggable from 'react-draggable';
import { ResizableBox } from 'react-resizable';
import 'react-resizable/css/styles.css';
import './App.css';

function App() {
  const [uploadedImage, setUploadedImage] = useState(null);
  const [imageUrl, setImageUrl] = useState('');
  const [hatVisible, setHatVisible] = useState(false);

  const handleImageUpload = (event) => {
    const file = event.target.files[0];
    if (file && file.type.startsWith('image/')) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setUploadedImage(reader.result);
        setImageUrl(URL.createObjectURL(file));
        console.log("Image uploaded:", URL.createObjectURL(file));
      };
      reader.onerror = (error) => {
        console.error("Error reading file:", error);
      };
      try {
        reader.readAsDataURL(file);
      } catch (error) {
        console.error("Failed to read file:", error);
      }
    } else {
      console.error("Invalid file type. Please upload an image.");
    }
  };

  const saveImage = () => {
    const canvas = document.createElement('canvas');
    const ctx = canvas.getContext('2d');
    const image = document.getElementById('uploaded-image');
    const hat = document.getElementById('hat');

    canvas.width = image.width;
    canvas.height = image.height;

    const hatRect = hat.getBoundingClientRect();
    const imageRect = image.getBoundingClientRect();

    ctx.drawImage(image, 0, 0);
    ctx.drawImage(
      hat,
      hatRect.left - imageRect.left,
      hatRect.top - imageRect.top,
      hat.clientWidth,
      hat.clientHeight
    );

    const link = document.createElement('a');
    link.download = 'profile_with_hat.png';
    link.href = canvas.toDataURL();
    link.click();
  };

  return (
    <div className="App">
      <h1>Add a Hat to Your Profile Picture</h1>
      <form>
        <input type="file" accept="image/jpeg, image/png, image/webp" onChange={handleImageUpload} required />
      </form>
      {uploadedImage && (
        <div id="profile-picture-container">
          <img id="uploaded-image" src={imageUrl} alt="Profile" onLoad={() => setHatVisible(true)} />
          {hatVisible && (
            <Draggable bounds="parent">
              <ResizableBox
                width={100}
                height={100}
                lockAspectRatio
                resizeHandles={['se']}
                style={{ zIndex: 1000, position: 'absolute' }}  // Ensuring high z-index and absolute position
              >
                <img id="hat" src="/static/hat.png" alt="Hat" style={{ width: '100%', height: '100%' }} onLoad={() => console.log("Hat loaded")} onError={(e) => console.error('Hat image load error', e)} />
              </ResizableBox>
            </Draggable>
          )}
        </div>
      )}
      {uploadedImage && (
        <button onClick={saveImage}>Save</button>
      )}
    </div>
  );
}

export default App;
