import React, { useState, useEffect, useRef } from 'react';
import Draggable from 'react-draggable';
import { ResizableBox } from 'react-resizable';
import 'react-resizable/css/styles.css';
import './App.css';

function App() {
  const [uploadedImage, setUploadedImage] = useState(null);
  const [imageUrl, setImageUrl] = useState('');
  const [hatVisible, setHatVisible] = useState(false);
  const hatRef = useRef(null);

  useEffect(() => {
    if (hatRef.current) {
      console.log("Hat element:", hatRef.current);
      console.log("Hat dimensions:", hatRef.current.getBoundingClientRect());
    }
  }, [hatVisible]);

  const handleImageUpload = (event) => {
    const file = event.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setUploadedImage(reader.result);
        setImageUrl(URL.createObjectURL(file));
        console.log("Image uploaded:", URL.createObjectURL(file));
      };
      reader.onerror = (error) => {
        console.error("Error reading file:", error);
      };
      try {
        reader.readAsDataURL(file);
      } catch (error) {
        console.error("Failed to read file:", error);
      }
    } else {
      console.error("No file selected or file type is not supported.");
    }
  };

  const saveImage = () => {
    const canvas = document.createElement('canvas');
    const ctx = canvas.getContext('2d');
    const image = document.getElementById('uploaded-image');
    const hat = document.getElementById('hat');

    canvas.width = image.width;
    canvas.height = image.height;

    const hatRect = hat.getBoundingClientRect();
    const imageRect = image.getBoundingClientRect();

    ctx.drawImage(image, 0, 0);
    ctx.drawImage(
      hat,
      hatRect.left - imageRect.left,
      hatRect.top - imageRect.top,
      hat.clientWidth,
      hat.clientHeight
    );

    const link = document.createElement('a');
    link.download = 'profile_with_hat.png';
    link.href = canvas.toDataURL();
    link.click();
  };

  return (
    <div className="App">
      <h1>Add a Hat to Your Profile Picture</h1>
      <form>
        <input type="file" accept="image/jpeg, image/png, image/webp" onChange={handleImageUpload} required />
      </form>
      {uploadedImage && (
        <div id="profile-picture-container">
          <img id="uploaded-image" src={imageUrl} alt="Profile" onLoad={() => setHatVisible(true)} onError={(e) => console.error('Uploaded image load error', e)} />
          {hatVisible && (
            <Draggable bounds="parent">
              <ResizableBox
                width={100}
                height={100}
                lockAspectRatio
                resizeHandles={['se']}
                minConstraints={[50, 50]}
                maxConstraints={[300, 300]}
                style={{ position: 'absolute', top: '10px', left: '10px', zIndex: 10, border: '1px solid red' }}  // Adjust initial position and added border for debugging
              >
                <img
                  id="hat"
                  ref={hatRef}
                  src="/static/hat.png"
                  alt="Hat"
                  style={{ width: '100%', height: '100%', display: 'block' }}
                  onLoad={() => console.log("Hat loaded")}
                  onError={(e) => console.error('Hat image load error', e)}
                />
              </ResizableBox>
            </Draggable>
          )}
        </div>
      )}
      {uploadedImage && (
        <button onClick={saveImage}>Save</button>
      )}
    </div>
  );
}

export default App;
