import React, { useState } from 'react';
import Draggable from 'react-draggable';
import { ResizableBox } from 'react-resizable';
import 'react-resizable/css/styles.css';
import './App.css';

function App() {
  const [uploadedImage, setUploadedImage] = useState(null);
  const [imageUrl, setImageUrl] = useState('');
  const [hatVisible, setHatVisible] = useState(false);

  const handleImageUpload = (event) => {
    const file = event.target.files[0];
    const reader = new FileReader();
    reader.onloadend = () => {
      setUploadedImage(reader.result);
      setImageUrl(URL.createObjectURL(file));
    };
    reader.readAsDataURL(file);
  };

  const saveImage = () => {
    const canvas = document.createElement('canvas');
    const ctx = canvas.getContext('2d');
    const image = document.getElementById('uploaded-image');
    const hat = document.getElementById('hat');

    canvas.width = image.width;
    canvas.height = image.height;

    ctx.drawImage(image, 0, 0);
    ctx.drawImage(
      hat,
      parseInt(hat.style.left, 10) - image.getBoundingClientRect().left,
      parseInt(hat.style.top, 10) - image.getBoundingClientRect().top,
      hat.clientWidth,
      hat.clientHeight
    );

    const link = document.createElement('a');
    link.download = 'profile_with_hat.png';
    link.href = canvas.toDataURL();
    link.click();
  };

  return (
    <div className="App">
      <h1>Add a Hat to Your Profile Picture</h1>
      <form>
        <input type="file" accept="image/jpeg, image/png, image/webp" onChange={handleImageUpload} required />
      </form>
      {uploadedImage && (
        <div id="profile-picture-container">
          <img id="uploaded-image" src={imageUrl} alt="Profile" onLoad={() => setHatVisible(true)} />
          {hatVisible && (
            <Draggable bounds="parent">
              <ResizableBox width={100} height={100} lockAspectRatio>
                <img id="hat" src="/static/hat.png" alt="Hat" style={{ width: '100%', height: '100%' }} />
              </ResizableBox>
            </Draggable>
          )}
        </div>
      )}
      {uploadedImage && (
        <button onClick={saveImage}>Save</button>
      )}
    </div>
  );
}

export default App;
